import org.hyperskill.hstest.v6.common.FileUtils;
import org.hyperskill.hstest.v6.stage.BaseStageTest;
import org.hyperskill.hstest.v6.testcase.CheckResult;
import org.hyperskill.hstest.v6.testcase.TestCase;

import java.util.List;

public class EncryptDecryptTest extends BaseStageTest<String> {
    public EncryptDecryptTest() {
        super(Main.class);
    }

    @Override
    public List<TestCase<String>> generate() {
        return List.of(
            new TestCase<String>()
                .addArguments(
                    "-mode", "enc",
                    "-key", "5",
                    "-in", "in.txt",
                    "-out", "output.txt"
                )
                .setAttach("\\jqhtrj%yt%m~ujwxpnqq&")
                .addFile("in.txt", "Welcome to hyperskill!"),

            new TestCase<String>()
                .addArguments(
                    "-key", "0",
                    "-mode", "enc",
                    "-in", "input.txt",
                    "-out", "output.txt"
                )
                .setAttach("Hello")
                .addFile("input.txt", "Hello"),

            new TestCase<String>()
                .addArguments(
                    "-key", "1",
                    "-in", "i.txt",
                    "-out", "output.txt",
                    "-mode", "enc"
                )
                .setAttach("123456789")
                .addFile("i.txt", "012345678"),

            new TestCase<String>()
                .addArguments(
                    "-mode", "dec",
                    "-in", "input.txt",
                    "-out", "output.txt",
                    "-key", "5"
                )
                .setAttach("Welcome to hyperskill!")
                .addFile("input.txt", "\\jqhtrj%yt%m~ujwxpnqq&"),

            new TestCase<String>()
                .addArguments(
                    "-mode", "dec",
                    "-key", "0",
                    "-in", "input.txt",
                    "-out", "output.txt"
                )
                .setAttach("Hi")
                .addFile("input.txt", "Hi"),

            new TestCase<String>()
                .addArguments(
                    "-mode", "dec",
                    "-key", "1",
                    "-in", "input.txt",
                    "-out", "output.txt"
                )
                .setAttach("111122223333")
                .addFile("input.txt", "222233334444")
        );
    }

    @Override
    public CheckResult check(String reply, String clue) {
        String output = FileUtils.readFile("output.txt");
        return new CheckResult(output.trim().equals(clue.trim()));
    }
}

