import encryptdecrypt.Main;
import org.hyperskill.hstest.v6.stage.BaseStageTest;
import org.hyperskill.hstest.v6.testcase.CheckResult;
import org.hyperskill.hstest.v6.testcase.PredefinedIOTestCase;
import org.hyperskill.hstest.v6.testcase.TestCase;

import java.util.List;

public class EncryptDecryptTest extends BaseStageTest<String> {
    public EncryptDecryptTest() {
        super(Main.class);
    }

    @Override
    public List<TestCase<String>> generate() {
        return List.of(
            new TestCase<String>().setAttach("dv ulfmw z givzhfiv!")
        );
    }

    @Override
    public CheckResult check(String reply, String clue) {
        reply = reply.trim();
        clue = clue.trim();
        boolean isCorrect = reply.trim().equals(clue.trim());
        if (isCorrect) {
            return CheckResult.TRUE;
        }
        if (reply.length() != clue.length()) {
            return CheckResult.FALSE(
                "You should output a line with length " +
                clue.length() + ". " + "You output a " +
                "line with length " + reply.length()
            );
        }
        for (int i = 0; i < clue.length(); i++) {
            if (reply.charAt(i) != clue.charAt(i)) {
                return CheckResult.FALSE(
                    "Your " + (i+1) + "-th character '" + reply.charAt(i) + "'" +
                    " is incorrect. The right one is '" + clue.charAt(i) + "'"
                );
            }
        }
        return CheckResult.TRUE;
    }
}

import encryptdecrypt.Main;
import org.hyperskill.hstest.v6.stage.BaseStageTest;
import org.hyperskill.hstest.v6.testcase.CheckResult;
import org.hyperskill.hstest.v6.testcase.TestCase;

import java.util.List;


class Attach {
    String original;
    int shift;
    String output;

    public Attach(String original, int shift, String output) {
        this.original = original;
        this.shift = shift;
        this.output = output;
    }
}

public class EncryptDecryptTest extends BaseStageTest<Attach> {
    public EncryptDecryptTest() {
        super(Main.class);
    }

    @Override
    public List<TestCase<Attach>> generate() {
        return List.of(
            new TestCase<Attach>()
                .setInput("welcome to hyperskill\n5")
                .setAttach(new Attach(
                    "welcome to hyperskill",
                    5,
                    "bjqhtrj yt mdujwxpnqq")),

            new TestCase<Attach>()
                .setInput("treasure\n10")
                .setAttach(new Attach(
                    "treasure",
                    10,
                    "dbokcebo"
                )),

            new TestCase<Attach>()
                .setInput("qdvdqvrxqwxrxwpvrxspvxiqgdiqarairpbiqqid\n12")
                .setAttach(new Attach(
                    "qdvdqvrxqwxrxwpvrxspvxiqgdiqarairpbiqqid",
                    12,
                    "cphpchdjcijdjibhdjebhjucspucmdmudbnuccup"
                )),

            new TestCase<Attach>()
                .setInput("y\n10")
                .setAttach(new Attach(
                    "y",
                    10,
                    "i"
                ))
        );
    }

    @Override
    public CheckResult check(String reply, Attach attach) {
        String clue = attach.output;
        reply = reply.trim();
        clue = clue.trim();
        boolean isCorrect = reply.equals(clue);
        if (isCorrect) {
            return CheckResult.TRUE;
        }
        if (reply.length() != clue.length()) {
            return CheckResult.FALSE(
                "You should output a line with length " +
                    clue.length() + ". " + "You output a " +
                    "line with length " + reply.length()
            );
        }
        for (int i = 0; i < clue.length(); i++) {
            if (reply.charAt(i) != clue.charAt(i)) {
                return CheckResult.FALSE(
                    "Your " + (i+1) + "-th character '" + reply.charAt(i) + "'" +
                        " is incorrect. \n" +
                    "The right one is '" + clue.charAt(i) + "'. \n" +
                        "Key is " + attach.shift
                );
            }
        }
        return CheckResult.TRUE;
    }
}

package encryptdecrypt;

import java.util.stream.Collectors;
import java.util.stream.IntStream;

public class Main {
    public static void main(String[] args) {
        Encryptor encryptor = new Encryptor();
        System.out.println(encryptor.encrypt("we found a treasure!"));
    }

    static class Encryptor {
        private static final String ALPHABET = IntStream.rangeClosed('a', 'z')
                .mapToObj(Character::toString)
                .collect(Collectors.joining());
        private static final int ALPHABET_LENGTH = ALPHABET.length();

        public String encrypt(String input) {
            StringBuilder builder = new StringBuilder();
            for (int i = 0; i < input.length(); i++) {
                int index = ALPHABET.indexOf(input.charAt(i));
                if (index >= 0) {
                    builder.append(ALPHABET.charAt(ALPHABET_LENGTH - index - 1));
                } else {
                    builder.append(input.charAt(i));
                }
            }
            return builder.toString();
        }
    }
}

